import React from 'react';
import styled from 'styled-components';

import data from '../data/campaigns.json';


const Container = styled.div`
    padding: 5px 50px 200px;
`
const Grid = styled.div`
    border: 2px solid grey;
    background-color: white;
`

const Header = styled.h1`
    color: white;
    text-align: left;
`

const Row = styled.div`
    display: flex;
    border: 1px solid grey;
    padding: 5px;
`

const Col = styled.div`
    flex: ${(props) => props.size};
    text-align: center;
`

const OpenCampaigns = () => {
    const campaigns = data.campaigns;

    return (
        <Container>
            <Header>Live Campaigns</Header>
            <Grid>
                <Row>
                    <Col size={2}>
                        Name
                    </Col>
                    <Col size={1}>
                        Target Segment
                    </Col>
                </Row>
                {campaigns.map(campaign => {
                    if (campaign.status === "Preview") {
                        let segmentName = '';
                        if (campaign.segment_id === 1) {
                            segmentName = data.segments[0].name;
                        } else if (campaign.segment_id === 2) {
                            segmentName = data.segments[1].name;
                        } else if (campaign.segment_id === 3) {
                            segmentName = data.segments[2].name;
                        }

                        return (
                            <Row key={campaign.id}>
                                <Col size={2}>
                                    {campaign.name}
                                </Col>
                                <Col size={1}>
                                    {segmentName}
                                </Col>
                            </Row>
                        )
                    } else {
                        return console.log("sent");
                    }
                })}
            </Grid>
        </Container>
    )
}

export default OpenCampaigns;
