import React, { useContext } from "react";
import { GlobalContext } from "../context/GlobalState";
import { Link } from "react-router-dom";
import { Table, Container, Row } from 'react-bootstrap';

import data from '../data/campaigns.json';


const OpenCampaigns = () => {
    const { campaigns, editCampaign } = useContext(GlobalContext);
    const openCampaigns = campaigns.filter(campaign => campaign.status != 'Sent');

    console.log("open", openCampaigns);
    const { segments, editSegments } = useContext(GlobalContext);


    return (
        <Container>
            <Row>
                <div className="col-12 mt-5">
                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th>Open Campaigns</th>
                                <th>Target Segment</th>
                            </tr>
                        </thead>
                        <tbody>
                            {campaigns.map(campaign => {
                                if (campaign.status === "Preview") {
                                    let segmentName = '';
                                    if (campaign.segment_id === 1) {
                                        segmentName = data.segments[0].name;
                                    } else if (campaign.segment_id === 2) {
                                        segmentName = data.segments[1].name;
                                    } else if (campaign.segment_id === 3) {
                                        segmentName = data.segments[2].name;
                                    }

                                    return (
                                        <tr key={campaign.id}>
                                            <td>{campaign.name}</td>
                                            <td>{segmentName}</td>
                                        </tr>
                                    )
                                } else {
                                    return console.log("sent");
                                }
                            })}
                        </tbody>
                    </Table>
                </div>
            </Row>
        </Container>
    )
}

export default OpenCampaigns;
