import React, { Fragment, useState, useContext, useEffect } from "react";
import { GlobalContext } from "../context/GlobalState";
import { useHistory, Link } from "react-router-dom";

import { Form, Button } from 'react-bootstrap';

export const EditCampaign = route => {
  console.log("route", route);
  let history = useHistory();
  const {segments} = React.useContext(GlobalContext);
  const { campaigns, EditCampaign } = useContext(GlobalContext);
  const [selectedCampaign, setSeletedCampaign] = useState({
    id: null,
    name: "",
    text: "",
    status: "Preview",
    segment_id: "",
    media: "",
    stats: null
  });
  const currentCampaignId = route.match.params.id;

  useEffect(() => {
    const campaignId = currentCampaignId;
    const selectedCampaign = campaigns.find(camp => camp.id === parseInt(campaignId));
    setSeletedCampaign(selectedCampaign);
  }, []);

  const onSubmit = e => {
    e.preventDefault();
    EditCampaign(selectedCampaign);
    history.push("/");
  };

  const handleOnChange = (campaignKey, value) =>
    setSeletedCampaign({ ...selectedCampaign, [campaignKey]: value });

  if (!selectedCampaign || !selectedCampaign.id) {
    alert("Id dont match !");
  }

  return (
    <Fragment>
      <div className="w-full max-w-sm container mt-20 mx-auto">
        <form onSubmit={onSubmit}>
          <div className="w-full mb-5">
            <label
              className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
              htmlFor="name"
            >
              Name of Campaign
            </label>
            <input
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:text-gray-600 focus:shadow-outline"
              value={selectedCampaign.name}
              onChange={e => handleOnChange("name", e.target.value)}
              type="text"
              placeholder="Enter name"
            />
          </div>
          <div className="w-full  mb-5">
            <label
              className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
              htmlFor="text"
            >
              Text
            </label>
            <input
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:text-gray-600 focus:shadow-outline"
              value={selectedCampaign.text}
              onChange={e => handleOnChange("text", e.target.value)}
              type="text"
              placeholder="Enter text"
            />
          </div>
          <div className="w-full  mb-5">
            <label
              className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
              htmlFor="media"
            >
              Media
            </label>
            <input
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:text-gray-600 focus:shadow-outline"
              value={selectedCampaign.media}
              onChange={e => handleOnChange("media", e.target.value)}
              type="text"
              placeholder="Enter media"
            />
          </div>
          <div className="flex items-center justify-between">
            <button className="block mt-5 bg-green-400 w-full hover:bg-green-500 text-white font-bold py-2 px-4 rounded focus:text-gray-600 focus:shadow-outline">
              Edit Campaign
            </button>
          </div>
          <div className="text-center mt-4 text-gray-500">
            <Link to="/">Cancel</Link>
          </div>
        </form>

        <Form>
          <Form.Group controlId="formBasicName">
            <Form.Label>Campaign Name</Form.Label>
            <Form.Control type="name" placeholder="Enter name" />
          </Form.Group>

          <Form.Group controlId="exampleForm.ControlSelect1">
            <Form.Label>Segment</Form.Label>
            <Form.Control as="select">
              {segments.map(segment => {
                  return (
                    <option key={segment.id}>{segment.name}</option>
                  )
              })}
            </Form.Control>
          </Form.Group>

          <Form.Group controlId="exampleForm.ControlTextarea1">
            <Form.Label>Message</Form.Label>
            <button type="button" className="btn btn-light btn-sm m-0" onClick={() => editCampaign(campaign.id)}>Add Photo or Gif</button>
            <Form.Control as="textarea" rows="3" />
          </Form.Group>

          <Button variant="primary" type="submit">
            Save
          </Button>
        </Form>
      </div>
    </Fragment>
  );
};
