import React from 'react';
import { Table, Container } from 'react-bootstrap';
import styled from 'styled-components';

import data from '../data/campaigns.json';

const Header = styled.h1`
    text-align: left;
`;

const LiveCampaigns = () => {
    const campaigns = data.campaigns;

    return (
        <Container>
            <Header>Live Campaigns</Header>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Amount Sent</th>
                        <th>Click Through Rates</th>
                    </tr>
                </thead>
                <tbody>
                    {campaigns.map(campaign => {
                        if (campaign.status === "Sent") {
                            console.log('sent');
                            return (
                                <tr key={campaign.id}>
                                    <td>{campaign.id}</td>
                                    <td>{campaign.name}</td>
                                    <td>{campaign.stats.sent}</td>
                                    <td>{campaign.stats.clicked}</td>
                                </tr>
                            )
                        } else {
                            return console.log("not sent");
                        }
                    })}
                </tbody>
            </Table>
        </Container>
    )
}

export default LiveCampaigns;
