import React from 'react';
import { Table, Container } from 'react-bootstrap';

import data from '../data/campaigns.json';

const OpenCampaigns = () => {
    const campaigns = data.campaigns;

    return (
        <Container>
            <Header>Live Campaigns</Header>
            <Grid>
                <Row>
                    <Col size={2}>
                        Name
                    </Col>
                    <Col size={1}>
                        Target Segment
                    </Col>
                </Row>
                {campaigns.map(campaign => {
                    if (campaign.status === "Preview") {
                        let segmentName = '';
                        if (campaign.segment_id === 1) {
                            segmentName = data.segments[0].name;
                        } else if (campaign.segment_id === 2) {
                            segmentName = data.segments[1].name;
                        } else if (campaign.segment_id === 3) {
                            segmentName = data.segments[2].name;
                        }

                        return (
                            <Row key={campaign.id}>
                                <Col size={2}>
                                    {campaign.name}
                                </Col>
                                <Col size={1}>
                                    {segmentName}
                                </Col>
                            </Row>
                        )
                    } else {
                        return console.log("sent");
                    }
                })}
            </Grid>
        </Container>
    )
}

export default OpenCampaigns;
