import React, { Fragment, useState, useContext } from "react";
import { GlobalContext } from "../context/GlobalState";
import { useHistory } from "react-router-dom";
import { Link } from "react-router-dom";

import { Form, Button, Container, Row } from 'react-bootstrap';

const H1 = styled.h1`
    color: #282c34;
    margin: 0 auto;
    padding: 50px 0;
    font-size: 48px;
    font-size: 48px;
    letter-spacing: -1.5px;
`

export const Addcampaign = () => {
  const [name, setName] = useState("");
  const [text, setText] = useState("");
  // const [status, setStatus] = useState("");
  const [segment_id, setSegment_id] = useState("");
  // const [media, setMedia] = useState("");
  const { AddCampaign, campaigns } = useContext(GlobalContext);
  let history = useHistory();

  const onSubmit = e => {
    e.preventDefault();
    const newCampaign = {
      id: campaigns.length + 1,
      name,
      text,
      // status,
      segment_id,
      media,
      // stats: null
    };
    AddCampaign(newCampaign);
    history.push("/");
  };

  return (
    // <Fragment>
    //   <div className="w-full max-w-sm container mt-20 mx-auto">
    //     <form onSubmit={onSubmit}>
    //       <div className="w-full mb-5">
    //         <label
    //           className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
    //           htmlFor="name"
    //         >
    //           Name of employee
    //         </label>
    //         <input
    //           className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:text-gray-600"
    //           value={name}
    //           onChange={e => setName(e.target.value)}
    //           type="text"
    //           placeholder="Enter name"
    //         />
    //       </div>
    //       <div className="w-full  mb-5">
    //         <label
    //           className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
    //           htmlFor="text"
    //         >
    //           Text
    //         </label>
    //         <input
    //           className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:text-gray-600 focus:shadow-outline"
    //           value={text}
    //           onChange={e => setText(e.target.value)}
    //           type="text"
    //           placeholder="Enter text"
    //         />
    //       </div>
    //       <div className="w-full  mb-5">
    //         <label
    //           className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
    //           htmlFor="media"
    //         >
    //           Media
    //         </label>
    //         <input
    //           className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:text-gray-600"
    //           value={media}
    //           onChange={e => setMedia(e.target.value)}
    //           type="media"
    //           placeholder="Enter media"
    //         />
    //       </div>
    //       <div className="flex items-center justify-between">
    //         <button className="mt-5 bg-green-400 w-full hover:bg-green-500 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
    //           Add Campaign
    //         </button>
    //       </div>
    //       <div className="text-center mt-4 text-gray-500">
    //         <Link to="/">Cancel</Link>
    //       </div>
    //     </form>
    //   </div>
    // </Fragment>

    <Container>
      <Row>
        <div className="col-6 mt-5">
          <H1>Add Campaign</H1>
          <Form onSubmit={onSubmit}>
            <Form.Group controlId="formBasicName">
              <Form.Label>Campaign Name</Form.Label>
              <Form.Control
                type="name"
                placeholder="Enter name"
                value={name}
                onChange={e => setName(e.target.value)}
              />
            </Form.Group>

            <Form.Group controlId="exampleForm.ControlSelect1">
              <Form.Label>Segment</Form.Label>
              <Form.Control as="select">
                {segments.map(segment => {
                    return (
                      <option key={segment.id} value={segment_id} onChange={e => setSegment_id(e.target.value)}>{segment.name}</option>
                    )
                })}
              </Form.Control>
            </Form.Group>

            <Form.Group controlId="exampleForm.ControlTextarea1">
              <Form.Label>Message</Form.Label>
              <button type="button" className="btn btn-sm m-2">Add Photo or Gif</button>
              <button type="button" className="btn btn-sm m-0">Add Tag</button>
              <Form.Control
                as="textarea"
                rows="3"
                value={text}
                onChange={e => setText(e.target.value)}
              />
            </Form.Group>

            <Button variant="primary" type="submit">
              Add Campaign
            </Button>

            <Link to="/">
                <button type="button" className="btn btn-link m-5">Cancel</button>
            </Link>
          </Form>
        </div>
      </Row>
    </Container>
  );
};
