import React from 'react';
import { Table, Container } from 'react-bootstrap';
import styled from 'styled-components';

import data from '../data/campaigns.json';

const Header = styled.h1`
    text-align: left;
`;

const OpenCampaigns = () => {
    const campaigns = data.campaigns;

    return (
        <Container>
            <div className="submarkets-table table-module table-container">
                <Header>Open Campaigns</Header>
                <Table className="table hover mt-16 type-body">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Target Segment</th>
                        </tr>
                    </thead>
                    <tbody>
                        {campaigns.map(campaign => {
                            if (campaign.status === "Preview") {
                                let segmentName = '';
                                if (campaign.segment_id === 1) {
                                    segmentName = data.segments[0].name;
                                } else if (campaign.segment_id === 2) {
                                    segmentName = data.segments[1].name;
                                } else if (campaign.segment_id === 3) {
                                    segmentName = data.segments[2].name;
                                }

                                return (
                                    <tr key={campaign.id}>
                                        <td>{campaign.name}</td>
                                        <td>{segmentName}</td>
                                    </tr>
                                )
                            } else {
                                return console.log("sent");
                            }
                        })}
                    </tbody>
                </Table>
            </div>
        </Container>
    )
}

export default OpenCampaigns;
