import React from 'react';
import styled from 'styled-components';

import data from '../data/campaigns.json';


const Container = styled.div`
    padding: 5px 50px;
`
const Grid = styled.div`
    border: 2px solid grey;
    background-color: white;
`

const Header = styled.h1`
    color: white;
    text-align: left;
`

const Row = styled.div`
    display: flex;
    border: 1px solid grey;
    padding: 5px;
`

const Col = styled.div`
    flex: ${(props) => props.size};
    text-align: center;
`

const GridComponent = () => {
    const campaigns = data.campaigns;

    return (
        <Container>
            <Header>Live Campaigns</Header>
            <Grid>
                <Row>
                    <Col size={2}>
                        Name
                    </Col>
                    <Col size={1}>
                        amount sent
                    </Col>
                    <Col size={1}>
                        click through rates
                    </Col>
                </Row>
                {campaigns.map(campaign => {
                    if (campaign.status === "Sent") {
                        console.log('sent');
                        return (
                            <Row key={campaign.id}>
                                <Col size={2}>
                                    {campaign.name}
                                </Col>
                                <Col size={1}>
                                    {campaign.stats.sent}
                                </Col>
                                <Col size={1}>
                                    {campaign.stats.clicked}
                                </Col>
                            </Row>
                        )
                    } else {
                        return <p key={campaign.id}>not sent</p>
                    }
                //   return (
                //     <List.Item  key={el.id}>
                //       <List.Content>
                //         {el.firstname} {el.lastname}
                //       </List.Content>
                //       <List.Content>{el.phone}</List.Content>
                //     </List.Item>
                //   );
                })}
            </Grid>
        </Container>
    )
}

export default GridComponent;
