import React, { useContext } from 'react';
import { GlobalContext } from "../context/GlobalState";
import { Link } from "react-router-dom";

import { Table, Container, Row } from 'react-bootstrap';


const LiveCampaigns = () => {
    const { campaigns, editCampaign } = useContext(GlobalContext);
    const liveCampaigns = campaigns.filter(campaign => campaign.status === 'Sent');

    return (
        <Container>
            <Row>
                <div className="col-12 mt-5">
                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th></th>
                                <th>Live Campaigns</th>
                                <th>Amount Sent</th>
                                <th>Click Through Rates</th>
                            </tr>
                        </thead>
                        <tbody>
                            {liveCampaigns.map(campaign => {
                                return (
                                    <tr key={campaign.id}>
                                        <td>
                                            <Link to={`/edit/${campaign.id}`}>
                                                <button type="button" className="btn btn-indigo btn-sm m-0" onClick={() => editCampaign(campaign.id)}>View Details</button>
                                            </Link>
                                        </td>
                                        <td>{campaign.name}</td>
                                        <td>{campaign.stats.sent}</td>
                                        <td>{campaign.stats.clicked}</td>
                                    </tr>
                                )
                            })}
                        </tbody>
                    </Table>
                </div>
            </Row>
        </Container>
    )
}

export default LiveCampaigns;
