import React, { useContext } from "react";
import { GlobalContext } from "../context/GlobalState";
import { Link } from "react-router-dom";
import { Table, Container, Row } from 'react-bootstrap';


const OpenCampaigns = () => {
    const { campaigns, editCampaign } = useContext(GlobalContext);
    const openCampaigns = campaigns.filter(campaign => campaign.status !== 'Sent');
    const { segments, editSegments } = useContext(GlobalContext);

    return (
        <Container>
            <Row>
                <div className="col-12 mt-5">
                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th></th>
                                <th>Open Campaigns</th>
                                <th>Target Segment</th>
                            </tr>
                        </thead>
                        <tbody>
                            {openCampaigns.map(campaign => {
                                const segment = segments.filter(segment => segment.id === campaign.segment_id);

                                return (
                                    <tr key={campaign.id}>
                                        <td>
                                            <Link to={`/edit/${campaign.id}`}>
                                                <button
                                                    onClick={() => editCampaign(campaign.id)}
                                                    className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-semibold mr-3 py-2 px-4 rounded-full inline-flex items-center"
                                                    >
                                                    <svg
                                                    xmlns="http://www.w3.org/2000/svg"
                                                    width="24"
                                                    height="24"
                                                    viewBox="0 0 24 24"
                                                    fill="none"
                                                    stroke="currentColor"
                                                    strokeWidth="2"
                                                    strokeLinecap="round"
                                                    strokeLinejoin="round"
                                                    className="feather feather-edit"
                                                    >
                                                    <path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7"></path>
                                                    <path d="M18.5 2.5a2.121 2.121 0 0 1 3 3L12 15l-4 1 1-4 9.5-9.5z"></path>
                                                    </svg>
                                                </button>
                                            </Link>
                                        </td>
                                        <td>{campaign.name}</td>
                                        <td>{segment[0].name}</td>
                                    </tr>
                                )
                            })}
                        </tbody>
                    </Table>
                </div>
            </Row>
        </Container>
    )
}

export default OpenCampaigns;
