import React from 'react';
import { Table, Container, Row } from 'react-bootstrap';


const OpenCampaigns = (campaigns, segments) => {

    return (
        <Container>
            <Row>
                <div class="col-12 mt-5">
                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th>Open Campaigns</th>
                                <th>Target Segment</th>
                            </tr>
                        </thead>
                        <tbody>
                            {campaigns.map(campaign => {
                                if (campaign.status === "Preview") {
                                    let segmentName = '';
                                    if (campaign.segment_id === 1) {
                                        segmentName = segments[0].name;
                                    } else if (campaign.segment_id === 2) {
                                        segmentName = segments[1].name;
                                    } else if (campaign.segment_id === 3) {
                                        segmentName = segments[2].name;
                                    }

                                    return (
                                        <tr key={campaign.id}>
                                            <td>{campaign.name}</td>
                                            <td>{segmentName}</td>
                                        </tr>
                                    )
                                } else {
                                    return console.log("sent");
                                }
                            })}
                        </tbody>
                    </Table>
                </div>
            </Row>
        </Container>
    )
}

export default OpenCampaigns;
