import React, { useState, useContext } from "react";
import { GlobalContext } from "../context/GlobalState";
import { useHistory } from "react-router-dom";
import { Link } from "react-router-dom";
import styled from 'styled-components';

import { Form, Button, Container, Row, Accordion } from 'react-bootstrap';

const H1 = styled.h1`
    color: #282c34;
    margin: 0 auto;
    padding: 50px 0;
    font-size: 48px;
    font-size: 48px;
    letter-spacing: -1.5px;
`

export const Addcampaign = () => {
  const [name, setName] = useState("");
  const [text, setText] = useState("");
  const [segment_id, setSegment_id] = useState("");
  const [media, setMedia] = useState("");
  const { AddCampaign, campaigns } = useContext(GlobalContext);
  const {segments} = React.useContext(GlobalContext);
  let history = useHistory();

  const onSubmit = e => {
    e.preventDefault();
    const newCampaign = {
      id: campaigns.length + 1,
      name,
      text,
      status: "Preview",
      segment_id,
      media,
      // stats: null
    };
    AddCampaign(newCampaign);
    history.push("/");
  };

  return (
    <Container>
      <Row>
        <div className="col-6 mt-5">
          <H1>Add Campaign</H1>
          <Form onSubmit={onSubmit}>
            <Form.Group controlId="formBasicName">
              <Form.Label>Campaign Name</Form.Label>
              <Form.Control
                type="name"
                placeholder="Enter name"
                value={name}
                onChange={e => setName(e.target.value)}
              />
            </Form.Group>

            <Form.Group controlId="exampleForm.ControlSelect1">
              <Form.Label>Segment</Form.Label>
              <Form.Control as="select">
                {segments.map(segment => {
                    return (
                      <option key={segment.id} value={segment_id} onChange={e => setSegment_id(e.target.value)}>{segment.name}</option>
                    )
                })}
              </Form.Control>
            </Form.Group>

            {/* <Form.Group controlId="exampleForm.ControlTextarea1">
              <Form.Label>Message</Form.Label>
              <button type="button" className="btn btn-sm m-2">Add Photo or Gif</button> */}

              <Accordion defaultActiveKey="0">
                <Form.Group controlId="exampleForm.ControlTextarea1">
                  <Form.Label>Message</Form.Label>
                    <Accordion.Toggle as={Button} variant="link" eventKey="0">
                      Add Photo or Gif
                    </Accordion.Toggle>
                  <Accordion.Collapse eventKey="0">
                    <Form.Control
                      type="media"
                      placeholder="Enter url"
                      value={media}
                      onChange={e => setMedia(e.target.value)}
                    />
                  </Accordion.Collapse>
                  <Form.Control
                    as="textarea"
                    rows="3"
                    value={text}
                    onChange={e => setText(e.target.value)}
                  />
                </Form.Group>
              </Accordion>

              {/* <button type="button" className="btn btn-sm m-0">Add Tag</button> */}
              {/* <Form.Group controlId="exampleForm.ControlTextarea1">
              <Form.Control
                as="textarea"
                rows="3"
                value={text}
                onChange={e => setText(e.target.value)}
              />
            </Form.Group> */}

            <Button variant="primary" type="submit">
              Add Campaign
            </Button>

            <Link to="/">
                <button type="button" className="btn btn-link m-5">Cancel</button>
            </Link>
          </Form>
        </div>
      </Row>
    </Container>
  );
};
