import React from 'react';
import { Table, Container, Row } from 'react-bootstrap';

import data from '../data/campaigns.json';


const LiveCampaigns = () => {
    const { campaigns, editCampaign } = useContext(GlobalContext);
    const openCampaigns = campaigns.filter(campaign => campaign.status === 'Sent');

    return (
        <Container>
            <Row>
                <div className="col-12 mt-5">
                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th>Live Campaigns</th>
                                <th>Amount Sent</th>
                                <th>Click Through Rates</th>
                            </tr>
                        </thead>
                        <tbody>
                            {openCampaigns.map(campaign => {
                                if (campaign.status === "Sent") {
                                    console.log('sent');
                                    return (
                                        <tr key={campaign.id}>
                                            <td>{campaign.name}</td>
                                            <td>{campaign.stats.sent}</td>
                                            <td>{campaign.stats.clicked}</td>
                                        </tr>
                                    )
                                } else {
                                    return console.log("not sent");
                                }
                            })}
                        </tbody>
                    </Table>
                </div>
            </Row>
        </Container>
    )
}

export default LiveCampaigns;
